#!/usr/bin/env python3


# Generated by 2.12.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    S = input()
    T = input()
    cand = []
    if len(S) < len(T):
        print("UNRESTORABLE")
        return
    for st in range(len(S) - len(T) + 1):
        for i in range(len(T)):
            if S[st + i] == "?":
                continue
            if S[st + i] != T[i]:
                break
        else:
            cand.append(st)
    if len(cand) == 0:
        print("UNRESTORABLE")
        return
    st = cand[-1]
    newS = S[:st] + T + S[(st+len(T)):] 
    print(newS.replace("?", "a"))
    return

if __name__ == '__main__':
    main()
