#!/usr/bin/env python3
import sys

def calc(num):
    tmp = 0
    if len(num) > 0:
        tmp = 2 - len(num)
        for i in num:
            tmp += 10 - int(i)
    return tmp

def solve(N: str):
    num = []
    ans = 0
    for ss in N:
        if int(ss) <= 5:
            ans += int(ss)
            ans += calc(num)
            num = []
        else:
            num.append(ss)

    ans += calc(num)
    print(ans)
    return


# Generated by 1.1.7.1 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    N = next(tokens)  # type: str
    solve(N)

if __name__ == '__main__':
    main()
