def main():
    H, W, N, M = map(int, input().split())
    field = [[0] * W for _ in range(H)]
    for _ in range(N):
        A, B = map(int, input().split())
        field[A - 1][B -1 ] = 1
    for _ in range(M):
        C, D = map(int, input().split())
        field[C-1][D -1] = -1

    up = [[0] * W for _ in range(H)]
    down = [[0] * W for _ in range(H)]
    right = [[0] * W for _ in range(H)]
    left = [[0] * W for _ in range(H)]

    for i in range(H - 1):
        for j in range(W):
            if (field[i][j] == 1 or up[i][j] == 1) and field[i + 1][j] != -1:
                up[i + 1][j] = 1

    for i in reversed(range(1, H)):
        for j in range(W):
            if (field[i][j] == 1 or down[i][j] == 1) and field[i - 1][j] != -1:
                down[i - 1][j] = 1

    for i in range(H):
        for j in range(W - 1):
            if (field[i][j] == 1 or right[i][j] == 1) and field[i][j + 1] != -1:
                right[i][j + 1] = 1

    for i in range(H):
        for j in reversed(range(1, W)):
            if (field[i][j] == 1 or left[i][j] == 1) and field[i][j - 1] != -1:
                left[i][j - 1] = 1          
    ans = 0
    for i in range(H):
        for j in range(W):
            if field[i][j] == 1 or up[i][j] == 1 or down[i][j] == 1 or right[i][j] == 1 or left[i][j] == 1:
                ans += 1
    print(ans)


