#!/usr/bin/env python3

MOD = 1000000007  # type: int
INF = 10 ** 16

# Generated by 2.12.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    H, W = map(int, input().split())
    W += 2
    G = ["#" * W]
    for _ in range(H):
        G.append("#" + input() + "#")
    G.append("#" * W)
    H += 2
    # print(G)
    dp = [[0] * W for _ in range(H)]
    sdpx = [[0] * W for _ in range(H)]
    sdpy = [[0] * W for _ in range(H)]
    sdpz = [[0] * W for _ in range(H)]
    dp[1][1] = 1
    sdpx[1][1] = 1
    sdpy[1][1] = 1
    sdpz[1][1] = 1
    for hh in range(H):
        for ww in range(W):
            if dp[hh][ww] != 0 or G[hh][ww] == "#":
                continue
            dp[hh][ww] = (sdpx[hh][ww - 1] + sdpy[hh - 1][ww] + sdpz[hh - 1][ww - 1]) % MOD
            sdpx[hh][ww] = (sdpx[hh][ww - 1] + dp[hh][ww]) % MOD
            sdpy[hh][ww] = (sdpy[hh - 1][ww] + dp[hh][ww]) % MOD
            sdpz[hh][ww] = (sdpz[hh - 1][ww - 1] + dp[hh][ww]) % MOD
    print(dp[H - 2][W - 2] % MOD)
    return

if __name__ == '__main__':
    main()
