def main():
    N, M, Q = map(int, input().split())
    vw = []
    for _ in range(N):
        ww, vv = (map(int, input().split()))
        vw.append((vv, ww))
    vw.sort(reverse=True)
    X = list(map(int, input().split()))
    for _ in range(Q):
        L, R = map(lambda i: int(i) - 1, input().split())
        used = [0] * M
        ans = 0
        for i in range(L, R + 1):
            used[i] = 1
        for vv, ww in vw:
            mini = -1
            for i in range(M):
                if X[i] >= ww and used[i] == 0 and \
                    (mini == -1 or X[mini] > X[i]):
                    mini = i
            if mini != -1:
                used[mini] = 1
                ans += vv
        print(ans)
    return

if __name__ == '__main__':
    main()
