#!/usr/bin/env python3
from collections import defaultdict

# Generated by 2.12.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    INF = 10 ** 10
    N = int(input())
    X = defaultdict(list)
    C = set()
    for _ in range(N):
        x,c = map(int,input().split())
        X[c].append(x)
        C.add(c)
    C = sorted(list(C))
    X[max(C) + 1].append(0)
    C.append(max(C) + 1)
    
    for k in X:
        X[k].sort()
    
    dp = [[INF] * 2 for _ in range(len(C) + 1)]
    dp[0] = [0, 0]
    l, r = 0, 0
    for i in range(len(C)):
        c = C[i]
        L, R = X[c][0], X[c][-1]
        dp[i + 1][0] = min(dp[i][0] + abs(R - L) + abs(l - R), dp[i][1] + abs(R - L) + abs(r - R))
        dp[i + 1][1] = min(dp[i][0] + abs(l - L) + abs(L - R), dp[i][1] + abs(r - L) + abs(L - R))
        l, r = L, R
    print(min(dp[-1]))
if __name__ == '__main__':
    main()
