#!/usr/bin/env python3
import copy

YES = "Yes"  # type: str
NO = "No"  # type: str

# Generated by 2.5.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    N = int(input())
    S, T = set(), set()
    for _ in range(N):
        S.add(tuple(map(int, input().split())))
    for _ in range(N):
        T.add(tuple(map(int, input().split())))
    for _ in range(4):
        mx, my = min(S)
        ss = set([(x - mx, y - my) for x, y in S])
        mx, my = min(T)
        tt = set([(x - mx, y - my) for x, y in T])
        if ss == tt:
            print(YES)
            return
        T = set([(y, -x) for x, y in T])
    print(NO)
    return
    

if __name__ == '__main__':
    main()
