#!/usr/bin/env python3
import sys

MOD = 1000000007  # type: int

def solve(S: str):
    chokudai = "chokudai"
    dp = [[0] * (len(S) + 1) for _ in range(9)]
    for i in range (len(S) + 1):
        dp[0][i] = 1
    for i in range(1, 9):
        for j in range(1, len(S) + 1):
            if chokudai[i - 1] == S[j - 1]:
                dp[i][j] = (dp[i - 1][j - 1] + dp[i][j - 1]) % MOD
            else:
                dp[i][j] = dp[i][j - 1]
    print(dp[-1][-1])


    return


# Generated by 2.5.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    S = next(tokens)  # type: str
    solve(S)

if __name__ == '__main__':
    main()
