#!/usr/bin/env python3


# Generated by 2.9.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    N = int(input())
    tasks = []
    for _ in range(N):
        l = list(map(int, input().split()))
        T, K = l[0], l[1]
        A = []
        if K != 0:
            A = [i - 1 for i in l[2:]]
        tasks.append((T, A))
    ans = 0
    from collections import deque
    q = deque()
    q.append(N - 1)
    fin = [0] * N
    while len(q) != 0:
        # print(q)
        # print(fin)
        next = q.pop()
        tt, aa = tasks[next]
        # print(tt, aa)
        if fin[next] == 0:
            ans += tt
        fin[next] = 1
        for aaa in aa:
            if fin[aaa] == 0:
                q.append(aaa)
    print(ans)
    return


if __name__ == '__main__':
    main()
