#!/usr/bin/env python3

YES = "Yes"  # type: str
NO = "No"  # type: str


# Generated by 2.12.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    N, M = map(int, input().split())
    meet = [[0 for _ in range(N)] for _ in range(N)]
    for _ in range(M):
        l = list(map(int, input().split()))
        for i in l[1:]:
            for j in l[1:]:
                meet[i - 1][j - 1] += 1
                meet[j - 1][i - 1] += 1
    for i in range(N):
        for j in range(N):
            if meet[i][j] < 2:
                print(NO)
                return
    print(YES)
    return    





if __name__ == '__main__':
    main()
