#!/usr/bin/env python3
import sys


def solve(N: int, S: str):
    S = list(S)
    ll = [list("axa"), list("ixi"), list("uxu"), list("exe"), list("oxo")]
    for i in range(N - 2):
        if S[i:(i + 3)] in ll:
            S[i] = "."
            S[i + 1] = "."
            S[i + 2] = "."
    print(*S, sep="")
    return


# Generated by 2.11.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    N = int(next(tokens))  # type: int
    S = next(tokens)  # type: str
    solve(N, S)

if __name__ == '__main__':
    main()
