#!/usr/bin/env python3
import sys
import bisect

def solve(N: int, M: int, a: "List[int]", b: "List[int]"):
    nodes = [[] for _ in range(N)]
    for i in range(M):
        nodes[a[i] - 1].append(b[i] - 1)
        nodes[b[i] - 1].append(a[i] - 1)
    ans = 0
    for i in range(N):
        nodes[i].sort()
        if bisect.bisect_left(nodes[i], i) == 1:
            ans += 1
    print(ans)
    return


# Generated by 2.5.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    N = int(next(tokens))  # type: int
    M = int(next(tokens))  # type: int
    a = [int()] * (M)  # type: "List[int]"
    b = [int()] * (M)  # type: "List[int]"
    for i in range(M):
        a[i] = int(next(tokens))
        b[i] = int(next(tokens))
    solve(N, M, a, b)

if __name__ == '__main__':
    main()
