#!/usr/bin/env python3
import sys


def solve(N: int, D: int, H: int, d: "List[int]", h: "List[int]"):
    if N == 1:
        print(max(0.0, (H * (D - d[0]) - D * (H - h[0])) / (D - d[0])))
        return
    mi = 0
    for i in range(1, N):
        if (H - h[mi]) * (D - d[i]) < (H - h[i]) * (D - d[mi]):
            continue
        else:
            mi = i
    print(max(0.0, (H * (D - d[mi]) - D * (H - h[mi])) / (D - d[mi])))
    return


# Generated by 2.2.0 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
def main():
    def iterate_tokens():
        for line in sys.stdin:
            for word in line.split():
                yield word
    tokens = iterate_tokens()
    N = int(next(tokens))  # type: int
    D = int(next(tokens))  # type: int
    H = int(next(tokens))  # type: int
    d = [int()] * (N)  # type: "List[int]"
    h = [int()] * (N)  # type: "List[int]"
    for i in range(N):
        d[i] = int(next(tokens))
        h[i] = int(next(tokens))
    solve(N, D, H, d, h)

if __name__ == '__main__':
    main()
